import "./styles.css";
import { Component } from "react";
import Person from "./components/Person";

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      network: [
        {
          id: 1,
          img:
            "https://res.cloudinary.com/dl26pbek4/image/upload/v1675071817/pexels-rodnae-productions-7348711_doe69b.jpg",
          email: "john@gmail.com",
          show: true
        },
        {
          id: 1,
          img:
            "https://res.cloudinary.com/dl26pbek4/image/upload/v1675071807/pexels-zaid-mohammed-15131063_bysy0s.jpg",
          email: "stephen@gmail.com",
          show: true
        },
        {
          id: 1,
          img:
            "https://res.cloudinary.com/dl26pbek4/image/upload/v1675071812/pexels-ali-kazal-14520051_qrdgym.jpg",
          email: "alex@gmail.com",
          show: true
        }
      ]
    };
  }


  handleClick=(index)=>{

    let newNetwork=this.state.network.filter((network,ind)=>{
      return ind!=index;
    })
    this.setState({network:newNetwork});
    //alert(`A person with email ${person.email} is removed from your network`);

    console.log(newNetwork);
  }
  handleClick = (i) => {
    const network = [...this.state.network];
    network[i].show = false;
    this.setState({ network });
  };


  render() {
    return (
      <div className="App">
        <h1>My Network</h1>
        <div className="list">
          {this.state.network.map((p, i) => (
            <Person key={i} person={p} index={i} handleClick={this.handleClick}/>
          ))}
        </div>
      </div>
    );
  }
}

export default App;
